from tkinter import Tk, Canvas, Entry, Text, Button, PhotoImage


OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / Path("./assets")
ASSETS_CARD_PATH = OUTPUT_PATH / Path("./assets/cards")



def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)
def relative_to_assets_cards(path: str) -> Path:
    return ASSETS_CARD_PATH / Path(path)

window = Tk()

window.geometry("1440x960")
window.configure(bg = "#1B1B1B")


canvas = Canvas(
    window,
    bg = "#1B1B1B",
    height = 960,
    width = 1440,
    bd = 0,
    highlightthickness = 0,
    relief = "ridge"
)

canvas.place(x = 0, y = 0)
def lol():
    global cards
    cards = {}
    for card in os.listdir("./assets/cards"):
        cards[card] = PhotoImage(file=relative_to_assets_cards(card))
    for j in cards:
        canvas.create_image(
            randint(1, 1440),
            randint(1, 960),
            image=cards[j])
